mapper.select:
select
from enrol_class where status = 1 and id in
#{item}

SELECT cla.id AS id, cla.`name` AS className, sub.`name` AS subjectName, cla.class_type_name AS classTypeName, cla.teach_mode_name AS teachModeName, cla.lesson_model AS lessonModel, cam.`name` AS campusName, cam.teaching_addr AS teachingAddr, cla.periods AS periods, cla.start_date AS startDate, cla.class_time AS classTime, cla.plan AS plan, cla.user_name AS userName, cla.status AS status, cla.create_time AS createTime, cla.brand_code AS brandName
FROM enrol_class cla,enrol_subject sub,enrol_campus cam
WHERE cla.subject_id=sub.id AND cla.campus_id=cam.id
AND cla.subject_id=#{subjectId}
AND cla.class_type_code=#{classTypeCode}
AND cla.teach_mode_code=#{teachModeCode}
AND cla.campus_id=#{campusId}
AND cla.id=#{id}
ORDER BY cla.create_time desc
